$(document).ready(function(){
    $('.product-thumb').each(function () {
        if ($(this).find('.percent-two').text() == '100%') {
            $(this).find('.percent-two').css('color','#6abd6b');
            $(this).find('.bg-cases-two').addClass('success');
            $(this).find('.btn-cases_main').addClass('success');
            $(this).find('.case-full').css('display','block');
        }
    });


});